mapper.select:
select 

from risk_jxl_credit
where name = #{name,jdbcType=VARCHAR} 
and idcard = #{idcard,jdbcType=VARCHAR}
and phone = #{phone,jdbcType=VARCHAR}
and DATE(query_time) = CURDATE()
LIMIT 0, 1

mapper.insert:
insert into risk_jxl_credit (id, name, idcard, 
  phone, query_time, result
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{idcard,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{queryTime,jdbcType=TIMESTAMP}, #{result,jdbcType=LONGVARBINARY}
  )
