mapper.select:
  	SELECT a.attr_name field_key,a.attr_value field_value,c.field_name field_name,c.data_provider provider
	FROM ${domain}_bp_attrs AS a
		INNER JOIN ${domain}_risk_bps AS b ON a.bp_id = b.bp_id
		INNER JOIN ${domain}_bp_meta_fields AS c ON b.bp_def_id = c.bp_def_id AND a.attr_name = c.field_key
	WHERE a.bp_id = #{bpId}
	AND a.attr_name=#{fieldKey}
	LIMIT 1;

  	SELECT a.dic_value 
  	FROM risk_data_dic AS a
		INNER JOIN ${domain}_risk_bps AS b ON a.bp_def_id = b.bp_def_id
	WHERE b.bp_id = #{bpId}
	AND a.dic_type = #{dicType}
	AND a.dic_key=#{dicKey}
	LIMIT 1;
